select * from employee.employees
where salary = (select distinct salary from employee.employees order by salary desc offset 1 row fetch next 1 row only)

select * from employee.employees
where salary = (select max(salary) from employee.employees where salary < (select max(salary) from employee.employees))

select emp_name, salary from 
(select emp_name, salary, dense_rank() over (order by salary desc) as rank from employee.employees) as ranked
where rank = 2



